import { combineReducers } from 'redux';

const reducersTask = (state=[],action) => {
    switch(action.type){
        case 'ADD_TASK':
        state = state.concat(action.payload);
        break;
        case 'DELETE_TASK':
        state = state.splice();
        state.splice(action.payload);
        break;
        case 'EDIT_TASK':
        break;
    }
    return state;
}

reducers = combineReducers({
    tasks:reducersTask
});

export default reducers;
