//Action Creators 
export const addTask = (task) =>{
    return{
        type:"ADD_TASK",
        payload: task
    };
};

export const deleteTask = (taskid) =>{
    return{
        type: 'DELETE_TASK',
        payload: taskid
    };
};

export const editTask = (edittask) =>{
    return{
        type:'EDIT_TASK',
        payload: edittask
    };
};

export default {addTask,deleteTask,editTask};
